CREATE TABLE [CargoGrado] ( [Id] int NOT NULL IDENTITY, [Nombre] varchar(255) NOT NULL, [Descripcion] text NOT NULL, [CargoId] int NOT NULL, [EstadoRegistro] char(10) NULL, [CreadoPor] varchar(255) NULL, [FechaCreacion] smalldatetime NULL, [ModificadoPor] varchar(255) NULL, [FechaModificacion] smalldatetime NULL, [EliminadoPor] varchar(255) NULL, [FechaEliminacion] smalldatetime NULL, CONSTRAINT [PK_CargoGrado] PRIMARY KEY ([Id]), CONSTRAINT [CK_CargoGrado_EstadoRegistro] CHECK (([EstadoRegistro]='Eliminado' OR [EstadoRegistro]='Inactivo' OR [EstadoRegistro]='Activo')), CONSTRAINT [FK_CargoGrado_Cargo_CargoId] FOREIGN KEY ([CargoId]) REFERENCES [Cargo] ([Id]) ON DELETE CASCADE );
GO
CREATE INDEX [IX_CargoGrado_CargoId] ON [CargoGrado] ([CargoId]);
GO
