CREATE TABLE [SolicitudPermiso] ( [Id] int NOT NULL IDENTITY, [FuncionarioId] int NOT NULL, [TipoAusentismoId] int NOT NULL, [FechaInicio] date NOT NULL, [FechaFin] date NOT NULL, [HoraSalida] time NULL, [HoraLlegada] time NULL, [Observaciones] text NULL, [Estado] char(20) NOT NULL, [Justificacion] text NULL, [EstadoRegistro] char(10) NULL, [CreadoPor] varchar(255) NULL, [FechaCreacion] smalldatetime NULL, [ModificadoPor] varchar(255) NULL, [FechaModificacion] smalldatetime NULL, [EliminadoPor] varchar(255) NULL, [FechaEliminacion] smalldatetime NULL, CONSTRAINT [PK_SolicitudPermiso] PRIMARY KEY ([Id]), CONSTRAINT [CK_SolicitudPermiso_EstadoRegistro] CHECK (([EstadoRegistro]='Eliminado' OR [EstadoRegistro]='Inactivo' OR [EstadoRegistro]='Activo')), CONSTRAINT [CK_SolicitudPermiso_Estado] CHECK (([Estado]='Aprobada' OR [Estado]='Autorizada' OR [Estado]='Cancelada' OR [Estado]='Rechazada' OR [Estado]='Solicitada')), CONSTRAINT [FK_SolicitudPermiso_Funcionario_FuncionarioId] FOREIGN KEY ([FuncionarioId]) REFERENCES [Funcionario] ([Id]) ON DELETE CASCADE, CONSTRAINT [FK_SolicitudPermiso_TipoAusentismo_TipoAusentismoId] FOREIGN KEY ([TipoAusentismoId]) REFERENCES [TipoAusentismo] ([Id]) ON DELETE CASCADE );
GO
