CREATE TABLE [TipoAportante] ( [Id] int NOT NULL IDENTITY, [EstadoRegistro] char(10) NULL, [CreadoPor] varchar(255) NULL, [FechaCreacion] smalldatetime NULL, [ModificadoPor] varchar(255) NULL, [FechaModificacion] smalldatetime NULL, [EliminadoPor] varchar(255) NULL, [FechaEliminacion] smalldatetime NULL, [Codigo] char(2) NOT NULL, [Nombre] varchar(255) NOT NULL, CONSTRAINT [PK_TipoAportante] PRIMARY KEY ([Id]), CONSTRAINT [CK_TipoAportante_EstadoRegistro] CHECK (([EstadoRegistro]='Eliminado' OR [EstadoRegistro]='Inactivo' OR [EstadoRegistro]='Activo')) );
GO CREATE UNIQUE INDEX [IX_TipoAportante_Codigo] ON [TipoAportante] ([Codigo]);
GO
CREATE UNIQUE INDEX [IX_TipoAportante_Nombre] ON [TipoAportante] ([Nombre]);
GO
