CREATE TABLE [TipoCotizante] ( [Id] int NOT NULL IDENTITY, [EstadoRegistro] char(10) NULL, [CreadoPor] varchar(255) NULL, [FechaCreacion] smalldatetime NULL, [ModificadoPor] varchar(255) NULL, [FechaModificacion] smalldatetime NULL, [EliminadoPor] varchar(255) NULL, [FechaEliminacion] smalldatetime NULL, [Codigo] char(2) NOT NULL, [Nombre] varchar(255) NOT NULL, CONSTRAINT [PK_TipoCotizante] PRIMARY KEY ([Id]), CONSTRAINT [CK_TipoCotizante_EstadoRegistro] CHECK (([EstadoRegistro]='Eliminado' OR [EstadoRegistro]='Inactivo' OR [EstadoRegistro]='Activo')) );
GO CREATE UNIQUE INDEX [IX_TipoCotizante_Codigo] ON [TipoCotizante] ([Codigo]);
GO
CREATE UNIQUE INDEX [IX_TipoCotizante_Nombre] ON [TipoCotizante] ([Nombre]);
GO
