CREATE TABLE [Subcategoria] ( [Id] int NOT NULL IDENTITY, [EstadoRegistro] char(10) NULL, [CreadoPor] varchar(255) NULL, [FechaCreacion] smalldatetime NULL, [ModificadoPor] varchar(255) NULL, [FechaModificacion] smalldatetime NULL, [EliminadoPor] varchar(255) NULL, [FechaEliminacion] smalldatetime NULL, [Nombre] varchar(255) NOT NULL, [CategoriaId] int NOT NULL, CONSTRAINT [PK_Subcategoria] PRIMARY KEY ([Id]), CONSTRAINT [FK_Subcategoria_Categoria_CategoriaId] FOREIGN KEY ([CategoriaId]) REFERENCES [Categoria] ([Id]) ON DELETE CASCADE );
GO
CREATE INDEX [IX_Subcategoria_CategoriaId] ON [Subcategoria] ([CategoriaId]);
GO
